    @Override
    public void exportarIndividuo(Individual ind, String fileId) {
        BinaryIndividual bi = (BinaryIndividual)ind;          
        File output= new File(fileId); 
        
        if (output.exists()) 
        {            
            FileOutputStream fos=null;        
            try {
                fos = new FileOutputStream(output, false);
            } catch (FileNotFoundException ex) {
                Logger.getLogger(MaxTriangleSum.class.getName()).log(Level.SEVERE, null, ex);
            }
            try {
                fos.close();
            } catch (IOException ex) {
                Logger.getLogger(MaxTriangleSum.class.getName()).log(Level.SEVERE, null, ex);
            }
        }        
        
        BufferedWriter bw = null;                         
            
        try {
            bw = new BufferedWriter(new FileWriter(output, true));
            bw.write(bi.toString());
//                bw.newLine();
            bw.flush();
        } catch (IOException ioe) {
            ioe.printStackTrace();
        } finally {                       // always close the file
            if (bw != null) try {
                bw.close();
            } catch (IOException ioe2) {
            // just ignore it
            }
        } // end try/catch/finally             
               
    }         
